import React, { useState } from 'react';

const SignupForm = ({ onSwitch }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = (event) => {
    event.preventDefault();
    let formErrors = {};

    if (!username) formErrors.username = 'All fields are required!';
    if (!password) formErrors.password = 'All fields are required!';
    if (!confirmPassword) formErrors.confirmPassword = 'Confirming password is required';
    if (!email) formErrors.email = 'Email is required';

    if (password !== confirmPassword) {
      formErrors.passwordsMatch = 'Passwords do not match!';
    }

    setErrors(formErrors);

    if (Object.keys(formErrors).length === 0) {
      console.log('Signup Submitted', { username, password, email });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Enter username"
        />
      </div>
      <div>
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter password"
        />
      </div>
      <div>
        <label>Confirm Password:</label>
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm password"
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter email"
        />
      </div>
      <div>
        <button type="submit">Signup</button>
      </div>
      <div> 
        <button type="button" onClick={onSwitch}>Switch to Login</button>
      </div>
    </form>
  );
};

export default SignupForm;
