import React, { useState } from 'react';

const SignupForm = ({ onSwitch }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = (event) => {
    event.preventDefault();
    let formErrors = {};
    let isValid = true;

    // Check for empty fields
    if (!username) {
      formErrors.username = 'Username is required';
      isValid = false;
    }
    if (!password) {
      formErrors.password = 'Password is required';
      isValid = false;
    }
    if (!confirmPassword) {
      formErrors.confirmPassword = 'Confirming password is required';
      isValid = false;
    }
    if (!email) {
      formErrors.email = 'Email is required';
      isValid = false;
    }

    // Check if passwords match
    if (password && confirmPassword && password !== confirmPassword) {
      formErrors.passwordsMatch = 'Passwords do not match!';
      isValid = false;
    }

    setErrors(formErrors);

    if (isValid) {
      console.log('Signup Submitted', { username, password, email });
      // Handle the actual form submission logic here (e.g., API call)
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit}>
        {errors.username && <div className="error">{errors.username}</div>}
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
        </div>

        {errors.password && <div className="error">{errors.password}</div>}
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter password"
          />
        </div>

        {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
        {errors.passwordsMatch && <div className="error">{errors.passwordsMatch}</div>}
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm password"
          />
        </div>

        {errors.email && <div className="error">{errors.email}</div>}
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter email"
          />
        </div>
        <div>
          <button type="submit">Signup</button>
          <button type="button" onClick={onSwitch}>Switch to Login</button>
        </div>
      </form>
    </div>
  );
};

export default SignupForm;
